import React,{useEffect,useState} from 'react';
import {useDispatch, useSelector} from 'react-redux';
import {searchPlayers} from '../redux/actions/counterActions';

//-//
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import {makeStyles,styled} from '@material-ui/core/styles';



const useStyles = makeStyles({
    root: {
        minWidth: 275,
        width: '100%',
        backgroundColor: '#00d1b2',
        color: '#fff',
        margin: '20px 0',
    },
    loser: {
        minWidth: 275,
        width: '100%',
        backgroundColor: '#ffdd57',
        color: '#fff',
        margin: '20px 0',
    },
    bullet: {
        display: 'inline-block',
        margin: '0 2px',
        transform: 'scale(0.8)',
    },
    title: {
        fontSize: 14,
    },
    pos: {
        marginBottom: 12,
    },
    flexCenter: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        margin: '100px 0',
        '& > div':{
            width: '40%',
            marginRight: '20px',
        }
    },
    columnCard:{
        width: '50%',
        margin: 'auto',
        padding: '.75rem',
        backgroundColor: '#00d1b2',
        color: '#fff',
    }

});

const Cards = ({apost}) => {
    const classes = useStyles();
    const dispatch = useDispatch();
    const {contestantOne} = useSelector((state)=>state.counter);
    const [player,setPlayer] = useState();

    useEffect(() => {
        /*console.log(apost);*/
        dispatch(searchPlayers(apost));
    }, []);

    useEffect(() => {
       if(contestantOne){
           setPlayer(contestantOne);
       }
    }, );



    return (
        <>
            {player ?
                ""
                :
                    <TableRow>
                        <TableCell>
                            Player Name
                        </TableCell>
                        <TableCell>
                            {player.ppg || 0}
                        </TableCell>
                        <TableCell align="right">{player.bpg || 0}</TableCell>
                        <TableCell align="right">{player.apg || 0}</TableCell>
                        <TableCell align="right">{player.ftm || 0}</TableCell>
                    </TableRow>
            }
        </>

    );
};

Cards.getInitialProps = async function({store}) {
    console.log('$$$$$$$');
}
export default Cards
