import React from 'react';
import { connect,useDispatch,useSelector } from 'react-redux';


import StatsDisplay from '../components/StatsDisplay';
import Search from '../components/Search';
import theme from '../src/Theme';
import {decrementCounter, incrementCounter,addPlayers} from '../redux/actions/counterActions';
import {makeStyles, MuiThemeProvider} from '@material-ui/core/styles';
import Typography from "@material-ui/core/Typography";



const useStyles = makeStyles({
    root: {
        backgroundImage: `url(${"../img/floor.png"})`
    },

});


const Index = (prop) => {
    console.log(prop);
    const dispatch = useDispatch();

    (function addToState() {
        dispatch(addPlayers(prop.stats['standard']));
    })()


    return (
        <MuiThemeProvider theme={theme}>
                   <div>

                   </div>
        </MuiThemeProvider>

    );
};


//life cycle method, run when load
Index.getInitialProps = async function({store}) {

    //npm i isomorphic-unfetch
    const res = await fetch('http://data.nba.net/10s/prod/v1/2019/players.json');
    const data = await res.json();

    return {
        stats: data.league
    };
}

export default Index;
