import { useImmer } from "use-immer"
import React, {useEffect} from 'react';
import { connect,useDispatch,useSelector } from 'react-redux';
import {searchPlayers,searchPlayerTwo} from '../redux/actions/counterActions';
import Cards from './Cards';
import TextField from '@material-ui/core/TextField';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import {makeStyles} from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';



const useStyles = makeStyles({
    root: {
        minWidth: 275,
        width: '100%'
    },
    columnCard:{
        width: '50%',
        margin: 'auto',
        padding: '.75rem',
        backgroundColor: '#00d1b2',
        color: '#fff',
    },
    bullet: {
        display: 'inline-block',
        margin: '0 2px',
        transform: 'scale(0.8)',
    },
    title: {
        fontSize: '32px',
        fontWeight: '600',
        textAlign: 'center',
        textTransform: 'capitalize',
        color: '#fff',
    },
    subTitle: {
        fontSize: '20px',
        fontWeight: '400',
        textAlign: 'center',
        textTransform: 'capitalize',
        color: '#fff',
    },
    pos: {
        marginBottom: 12,
    },
    flexCenter: {
        display: 'flex',
        justifyContent: 'start',
        alignItems: 'center',
        '& > div':{
            width: '40%',
            marginRight: '20px',
        }
    },
    tableStyle: {
        width: '50%',
        margin: 'auto',
    }

});

const Search = (props) => {
    const classes = useStyles();
    const [state, setState] = useImmer({
        searchTerm: "",
        teams: [
            "sixers", "blazers", "bucks", "bulls", "cavaliers",
            "celtics", "clippers", "grizzlies", "hawks", "heat",
            "hornets", "jazz", "kings", "knicks", "lakers",
            "magic", "mavericks", "nets", "nuggets", "pacers",
            "pelicans", "pistons", "raptors", "rockets", "spurs",
            "suns", "thunder", "timberwolves", "warriors", "wizards"
        ],
        searchResults: [
            "sixers", "blazers", "bucks", "bulls", "cavaliers",
            "celtics", "clippers", "grizzlies", "hawks", "heat",
            "hornets", "jazz", "kings", "knicks", "lakers",
            "magic", "mavericks", "nets", "nuggets", "pacers",
            "pelicans", "pistons", "raptors", "rockets", "spurs",
            "suns", "thunder", "timberwolves", "warriors", "wizards"
        ]
    });

    function  handleInputChange() {

    }

    function renderResults(searchResults){
        return searchResults.map((team, index) => {
            return (
                <Fade key={index}>
                    <Link to={`/${team}/roster`}>
                        <div className="team hvr-underline-from-center hvr-grow-shadow">
                            <img src={`/images/teams/${team}.png`} alt={team} />
                        </div>
                    </Link>
                </Fade>
            )
        })
    }


    return (
        <React.Fragment>
            <div className="main-container">
                <div className="search-container">
                    <TextField
                        className={classes.root}
                        id="first"
                        label="Search your team"
                        onChange={handleInputChange}/>
                </div>
                <div className="teams-container">
                    {renderResults(state.searchResults)}
                </div>
            </div>
        </React.Fragment>


    );

};

Search.getInitialProps = async function({store}) {

}

export default Search;
