import {INCREMENT_COUNTER,
    DECREMENT_COUNTER,
    SAVE_PLAYERS,
    GET_PLAYERS,
    SEARCH_PLAYERS} from './types'
import {useDispatch} from 'react-redux';


//Action Creator
export const incrementCounter = () => ({
    type: INCREMENT_COUNTER
});

export const decrementCounter = () => ({
    type: DECREMENT_COUNTER
});

export const addPlayers = basketBallPlayers => ({
    type: SAVE_PLAYERS,
    payload: basketBallPlayers
});

export const searchPlayers = text => async dispatch => {
    try{
        const res = await fetch(`http://data.nba.net/10s/prod/v1/2019/players/${text}_profile.json`);
        const data = await res.json();

        dispatch({
            type: SEARCH_PLAYERS,
            payload: data.league.standard.stats.careerSummary
        })

    }catch (e) {
        console.log(e);
    }

};

export const getPlayers = () => async dispatch => ({
    type: GET_PLAYERS
});
