import React,{useEffect,useState} from 'react';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import {makeStyles,styled} from '@material-ui/core/styles';


const useStyles = makeStyles({
    root: {
        minWidth: 275,
        width: '100%',
        backgroundColor: '#00d1b2',
        color: '#fff',
        margin: '20px 0',
    },
    loser: {
        minWidth: 275,
        width: '100%',
        backgroundColor: '#ffdd57',
        color: '#fff',
        margin: '20px 0',
    },
    bullet: {
        display: 'inline-block',
        margin: '0 2px',
        transform: 'scale(0.8)',
    },
    title: {
        fontSize: 14,
    },
    pos: {
        marginBottom: 12,
    },
    flexCenter: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        margin: '100px 0',
        '& > div':{
            width: '40%',
            marginRight: '20px',
        }
    },
    columnCard:{
        width: '50%',
        margin: 'auto',
        padding: '.75rem',
        backgroundColor: '#00d1b2',
        color: '#fff',
    }

});

const Cards = ({playerOneInfo,playerTwoInfo}) => {
    const classes = useStyles();
    const [playerone,setPlayerone] = useState(0);
    const [playertwo,setPlayertwo] = useState(0);

    useEffect(()=>{
        if(playerOneInfo !== undefined){
            setPlayerone(playerOneInfo);
        }

    },[playerOneInfo])

    useEffect(()=>{
        if(playerTwoInfo !== undefined){
            setPlayertwo(playerTwoInfo);
        }
    },[playerTwoInfo])

    const LoserCard = styled(Card)({
        minWidth: 275,
        width: '100%',
        backgroundColor: '#ffdd57',
        color: '#fff',
        margin: '20px 0'

    });

    const WinnerCard = styled(Card)({
        minWidth: 275,
        width: '100%',
        backgroundColor: '#00d1b2',
        color: '#fff',
        margin: '20px 0',
    });

    const isUndefine = playertwo !== undefined;





       const playerA = (
           <div>
               <div>
                   <div>
                       <WinnerCard>
                           <CardContent>
                               <Typography variant="h5" gutterBottom>
                                   POINTS PER GAME
                               </Typography>
                               <Typography variant="h5" component="h2">
                                   {playerone.ppg}
                               </Typography>
                           </CardContent>
                       </WinnerCard>
                   </div>
                   <div>
                       <Card className={classes.root}>
                           <CardContent>
                               <Typography variant="h5" gutterBottom>
                                   ASSISTS PER GAME
                               </Typography>
                               <Typography variant="h5" component="h2">
                                   {playerone.bpg}
                               </Typography>
                           </CardContent>
                       </Card>
                   </div>

                   <div>
                       <Card className={classes.root}>
                           <CardContent>
                               <Typography variant="h5" gutterBottom>
                                   BLOCKS  PER GAME
                               </Typography>
                               <Typography variant="h5" component="h2">
                                   {playerone.bpg}
                               </Typography>
                           </CardContent>
                       </Card>
                   </div>

                   <div>
                       <Card className={classes.root}>
                           <CardContent>
                               <Typography variant="h5" gutterBottom>
                                   STEALS  PER GAME
                               </Typography>
                               <Typography variant="h5" component="h2">
                                   {playerone.spg}
                               </Typography>
                           </CardContent>
                       </Card>
                   </div>

               </div>
           </div>
       );

       const playerB = (
           <div>
               <React.Fragment>
                   <div>
                       <Card className='loser'>
                           <CardContent>
                               <Typography variant="h5" gutterBottom>
                                   POINTS PER GAME
                               </Typography>
                               <Typography variant="h5" component="h2">
                                   {playertwo.ppg}
                               </Typography>
                           </CardContent>
                       </Card>
                   </div>
                   <div>
                       <Card className={classes.root}>
                           <CardContent>
                               <Typography variant="h5" gutterBottom>
                                   ASSISTS PER GAME
                               </Typography>
                               <Typography variant="h5" component="h2">
                                   {playertwo.bpg}
                               </Typography>
                           </CardContent>
                       </Card>
                   </div>

                   <div>
                       <Card className={classes.root}>
                           <CardContent>
                               <Typography variant="h5" gutterBottom>
                                   BLOCKS  PER GAME
                               </Typography>
                               <Typography variant="h5" component="h2">
                                   {playertwo.bpg}
                               </Typography>
                           </CardContent>
                       </Card>
                   </div>

                   <div>
                       <Card className={classes.root}>
                           <CardContent>
                               <Typography variant="h5" gutterBottom>
                                   STEALS  PER GAME
                               </Typography>
                               <Typography variant="h5" component="h2">
                                   {playertwo.spg}
                               </Typography>
                           </CardContent>
                       </Card>
                   </div>

               </React.Fragment>

           </div>
       );


    return (
        <React.Fragment>
            {playerone.min !== undefined ? playerA: null}
            {playertwo.min !== undefined ? playerB: null}
            <style jsx>{`
                .loser{
                min-width: 275px,
                width: 100%,
                background-color: #ffdd57,
                color: #fff,
                margin: 20px 0,
                }
        `}</style>
        </React.Fragment>

    );
};

Cards.getInitialProps = async function({store}) {

}
export default Cards
