import { useImmer } from "use-immer"
import {useEffect} from 'react';
import { connect,useSelector } from 'react-redux';

import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';



const useStyles = makeStyles({
    root: {
        minWidth: 275,
        width: '100%'
    },
    bullet: {
        display: 'inline-block',
        margin: '0 2px',
        transform: 'scale(0.8)',
    },
    title: {
        fontSize: 14,
    },
    pos: {
        marginBottom: 12,
    },
    flexCenter: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        margin: '100px 0',
    }

});

const Search = (props) => {
    const classes = useStyles();
    const [state, setState] = useImmer({
        searchTerm: "",
        results: [],
        show: "neither",
        requestCount: 0,
    });
    const  {counter} = useSelector((state)=>state.counter);

    async function fff() {
        const res = await fetch('http://data.nba.net/10s/prod/v1/2019/players/203500_profile.json');
        const data = await res.json();
        console.log(data.league.standard.stats.careerSummary.apg);
    }
    fff();



    function handleInput(e) {
        const value = e.target.value
        console.log(value);
        setState((draft) => {
            draft.searchTerm = value
        })
    }

    return (
        <div className={`flex-center`}>
            <div>

                <form noValidate autoComplete="off">
                    <TextField
                        className={classes.root}
                        id="standard-basic"
                        label="Standard"
                        onChange={handleInput}
                        defaultValue="Hello World"/>
                </form>

                <Card className={classes.root}>
                    <CardContent>
                        <Typography className={classes.title} color="textSecondary" gutterBottom>
                            Word of the Day
                        </Typography>
                        <Typography variant="h5" component="h2">
                            Name
                        </Typography>
                        <Typography className={classes.pos} color="textSecondary">
                            adjective
                        </Typography>
                        <Typography variant="body2" component="p">
                            well meaning and kindly.
                            <br />
                            {'"a benevolent smile"'}
                        </Typography>
                    </CardContent>

                    <CardActions>
                        <Button size="small">Learn More</Button>
                    </CardActions>
                </Card>
                <h1>{counter}</h1>

            </div>

            <div>

                <form noValidate autoComplete="off">
                    <TextField className={classes.root} id="standard-basic" label="Standard" onChange={handleInput} />

                </form>

                <Card className={classes.root}>
                    <CardContent>
                        <Typography className={classes.title} color="textSecondary" gutterBottom>
                            Word of the Day
                        </Typography>
                        <Typography variant="h5" component="h2">
                            Name
                        </Typography>
                        <Typography className={classes.pos} color="textSecondary">
                            adjective
                        </Typography>
                        <Typography variant="body2" component="p">
                            well meaning and kindly.
                            <br />
                            {'"a benevolent smile"'}
                        </Typography>
                    </CardContent>

                    <CardActions>
                        <Button size="small">Learn More</Button>
                    </CardActions>
                </Card>

            </div>

            <style jsx>{`
            
                 .flex-center > div{
                     width: 40%;
                     margin-right: 20px;
                 }
               
            `}
            </style>
        </div>

    );

};

Search.getInitialProps = async function({store}) {

}

export default Search;
