import React,{useEffect,useState} from 'react';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import {makeStyles} from '@material-ui/core/styles';


const useStyles = makeStyles({
    root: {
        minWidth: 275,
        width: '100%',
        backgroundColor: '#00d1b2',
        color: '#fff',
        margin: '20px 0',
    },
    bullet: {
        display: 'inline-block',
        margin: '0 2px',
        transform: 'scale(0.8)',
    },
    title: {
        fontSize: 14,
    },
    pos: {
        marginBottom: 12,
    },
    flexCenter: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        margin: '100px 0',
        '& > div':{
            width: '40%',
            marginRight: '20px',
        }
    },
    columnCard:{
        width: '50%',
        margin: 'auto',
        padding: '.75rem',
        backgroundColor: '#00d1b2',
        color: '#fff',
    }

});

const Cards = ({playerOneInfo,playerTwoInfo}) => {
    const classes = useStyles();
    const [playerone,setPlayerone] = useState({});
    const [playertwo,setPlayertwo] = useState({});

    useEffect(()=>{
        if(playerOneInfo !== undefined){
            setPlayerone(playerOneInfo);
        }

    },[playerOneInfo])

    useEffect(()=>{
        if(playerTwoInfo !== undefined){
            setPlayertwo(playerTwoInfo);
        }
    },[playerTwoInfo])


      /*let the games begin*/
      if(playerone.min !== undefined && playertwo.min !== undefined){
          
      }

       const playerA = (
           <div>
               <React.Fragment>
                   <div>
                       <Card className={classes.root}>
                           <CardContent>
                               <Typography variant="h5" gutterBottom>
                                   POINTS PER GAME
                               </Typography>
                               <Typography variant="h5" component="h2">
                                   {playerone.ppg}
                               </Typography>
                           </CardContent>
                       </Card>
                   </div>
                   <div>
                       <Card className={classes.root}>
                           <CardContent>
                               <Typography variant="h5" gutterBottom>
                                   ASSISTS PER GAME
                               </Typography>
                               <Typography variant="h5" component="h2">
                                   {playerone.bpg}
                               </Typography>
                           </CardContent>
                       </Card>
                   </div>

                   <div>
                       <Card className={classes.root}>
                           <CardContent>
                               <Typography variant="h5" gutterBottom>
                                   BLOCKS  PER GAME
                               </Typography>
                               <Typography variant="h5" component="h2">
                                   {playerone.bpg}
                               </Typography>
                           </CardContent>
                       </Card>
                   </div>

                   <div>
                       <Card className={classes.root}>
                           <CardContent>
                               <Typography variant="h5" gutterBottom>
                                   STEALS  PER GAME
                               </Typography>
                               <Typography variant="h5" component="h2">
                                   {playerone.spg}
                               </Typography>
                           </CardContent>
                       </Card>
                   </div>

               </React.Fragment>
           </div>
       );

       const playerB = (
           <div>
               <React.Fragment>
                   <div>
                       <Card className={classes.root}>
                           <CardContent>
                               <Typography variant="h5" gutterBottom>
                                   POINTS PER GAME
                               </Typography>
                               <Typography variant="h5" component="h2">
                                   {playertwo.ppg}
                               </Typography>
                           </CardContent>
                       </Card>
                   </div>
                   <div>
                       <Card className={classes.root}>
                           <CardContent>
                               <Typography variant="h5" gutterBottom>
                                   ASSISTS PER GAME
                               </Typography>
                               <Typography variant="h5" component="h2">
                                   {playertwo.bpg}
                               </Typography>
                           </CardContent>
                       </Card>
                   </div>

                   <div>
                       <Card className={classes.root}>
                           <CardContent>
                               <Typography variant="h5" gutterBottom>
                                   BLOCKS  PER GAME
                               </Typography>
                               <Typography variant="h5" component="h2">
                                   {playertwo.bpg}
                               </Typography>
                           </CardContent>
                       </Card>
                   </div>

                   <div>
                       <Card className={classes.root}>
                           <CardContent>
                               <Typography variant="h5" gutterBottom>
                                   STEALS  PER GAME
                               </Typography>
                               <Typography variant="h5" component="h2">
                                   {playertwo.spg}
                               </Typography>
                           </CardContent>
                       </Card>
                   </div>

               </React.Fragment>
           </div>
       );


    return (
        <React.Fragment>
            {playerone.min !== undefined ? playerA: null}
            {playertwo.min !== undefined ? playerB: null}
        </React.Fragment>

    );
};

Cards.getInitialProps = async function({store}) {

}
export default Cards
