import {INCREMENT_COUNTER,DECREMENT_COUNTER,SAVE_PLAYERS } from './types'


//Action Creator
export const incrementCounter = () => ({
    type: INCREMENT_COUNTER
});

export const decrementCounter = () => ({
    type: DECREMENT_COUNTER
});

export const addPlayers = (basketBallPlayers) => async dispatch => ({
    type: SAVE_PLAYERS,
    payload: basketBallPlayers
});

export const getPlayers = () => async dispatch => ({
    type: SAVE_PLAYERS
});
