import {INCREMENT_COUNTER,
    DECREMENT_COUNTER,
    SAVE_PLAYERS,
    GET_PLAYERS,
    SEARCH_PLAYER_ONE,
    SEARCH_PLAYER_TWO} from '../actions/types'



const initialState = {
    basketBallPlayers: null,
    counter: 0,
    contestantOne: null,
    contestantTwo: null,
    careerSummary: null
};

const counterReducer = (state= initialState, action) => {
    switch (action.type) {
        case SAVE_PLAYERS:
            return {
                ...state,
                basketBallPlayers: action.payload
            };
        case GET_PLAYERS:
            return {
                ...state,
                basketBallPlayers: action.payload
            };
        case SEARCH_PLAYER_ONE:
            return {
                ...state,
                contestantOne: action.payload
            };

        case SEARCH_PLAYER_TWO:
            return {
                ...state,
                contestantTwo: action.payload
            };


        case INCREMENT_COUNTER:
            return {...state, counter: state.counter + 1};
        case DECREMENT_COUNTER:
            return {...state, counter: state.counter - 1};
        default:
            return {...state};
    }
};

export default counterReducer;
