import { useImmer } from "use-immer"
import React, {useEffect} from 'react';
import { connect,useDispatch,useSelector } from 'react-redux';
import {searchPlayers,searchPlayerTwo} from '../redux/actions/counterActions';
import Cards from './Cards';
import TextField from '@material-ui/core/TextField';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import {makeStyles} from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';



const useStyles = makeStyles({
    root: {
        minWidth: 275,
        width: '100%'
    },
    columnCard:{
        width: '50%',
        margin: 'auto',
        padding: '.75rem',
        backgroundColor: '#00d1b2',
        color: '#fff',
    },
    bullet: {
        display: 'inline-block',
        margin: '0 2px',
        transform: 'scale(0.8)',
    },
    title: {
        fontSize: '32px',
        fontWeight: '600',
        textAlign: 'center',
        textTransform: 'capitalize',
        color: '#fff',
    },
    subTitle: {
        fontSize: '20px',
        fontWeight: '400',
        textAlign: 'center',
        textTransform: 'capitalize',
        color: '#fff',
    },
    pos: {
        marginBottom: 12,
    },
    flexCenter: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        margin: '100px 0',
        '& > div':{
            width: '40%',
            marginRight: '20px',
        }
    },
    tableStyle: {
        width: '50%',
        margin: 'auto',
    }

});

const Search = (props) => {
    const classes = useStyles();
    const [state, setState] = useImmer({
        searchTerm: "",
        results: [],
        playerOne: null,
        playerTwo: null,
        allPlayers: [],
        id: null,
        personObject: null,
        requestCount: 0,
    });

    const dispatch = useDispatch();
    const  {basketBallPlayers,contestantOne,contestantTwo} = useSelector((state)=>state.counter);

    /*get all players on first render*/
    useEffect(()=>{
        setState((draft) => {
            draft.allPlayers = basketBallPlayers;
        })
    },[])

    /*trigger when contestantOne is changed*/
    useEffect(()=> {
        setState((draft) => {
            draft.playerOne = contestantOne;
        })
    },[contestantOne])

    /*trigger when contestantTwo is changed*/
    useEffect(()=>{
        setState((draft) => {
            draft.playerTwo = contestantTwo;
        })
    },[contestantTwo])



    /*trigger when personObject is change*/
    useEffect(()=>{
       if(state.personObject){
           console.log(state.personObject);
           const newPlayer = {
               id: state.id,
               value: state.personObject.personId
           }

           setState((draft) => {
               draft.results = [];
               draft.searchTerm = state.personObject.firstName;
           })
           dispatch(searchPlayers(newPlayer))
       }


    },[state.personObject])

    /*trigger when search term as change*/
    useEffect(()=>{
        if(state.searchTerm === ""){
            setState((draft) => {
                draft.searchTerm = "";
            })
        }
    },[state.searchTerm])


    /*
    Filter results array
    set result and id
     */
    function handleInput(e) {
        const value = e.target.value
        const newId = e.target.id

        let result = state.allPlayers.filter(n=>{
            return n.firstName.toLowerCase().includes(value.toLowerCase());
        })

        if(result.length <=0){
            setState((draft) => {
                draft.results = []
                draft.searchTerm= ""
            })
        } else {
            setState((draft) => {
                draft.results = result
                draft.id = newId;
                draft.searchTerm= value
            })
        }
    }





    return (
        <React.Fragment>
            <div className={classes.flexCenter}>
                <div>
                    <form noValidate autoComplete="on">
                        <TextField
                            className={classes.root}
                            id="first"
                            label="Player1"
                            onChange={handleInput}
                            />
                    </form>
                    {Boolean(state.playerTwo !== null && state.playerOne !== null) &&(
                        <Cards playerOneInfo={state.playerOne} playerTwoInfo={state.playerTwo}/>
                    )}
                </div>

                <div>

                    <form noValidate autoComplete="off">
                        <TextField
                            className={classes.root}
                            id="second"
                            label="Player2"
                            onChange={handleInput}
                            />
                    </form>

                    {Boolean(state.playerTwo !== null && state.playerOne !== null) &&(
                        <Cards playerOneInfo={state.playerOne} playerTwoInfo={state.playerTwo}/>
                    )}


                </div>

            </div>

            {state.searchTerm === "" && state.results.length === 0 &&
            <Card className={classes.columnCard}>
                <CardContent>
                    <Typography
                        className={classes.title}
                        gutterBottom>
                        Search For A Player On Both Sides
                    </Typography>

                    <Typography
                        className={classes.subTitle}
                        variant="h5" /*h5 in size*/
                        component="h1" /*but and actual h1*/
                    >
                        We will tell you which is best!
                    </Typography>

                </CardContent>
            </Card>
            }

            {state.results.length > 0 &&
            <TableContainer component={Paper} className={classes.tableStyle}>
                <Table className={classes.table} aria-label="simple table">
                    <TableHead>
                        <TableRow>
                            <TableCell>FirstName</TableCell>
                            <TableCell align="right">LastName</TableCell>
                            <TableCell align="right">Country</TableCell>
                            <TableCell align="right">College</TableCell>
                            <TableCell align="right">Years Pro</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {state.results.map((name,index) =>(
                            <TableRow key={name.personId} onClick={()=>setState((draft) => {draft.personObject = name})}>
                                <TableCell component="th" scope="row">
                                    {name.firstName}
                                </TableCell>
                                <TableCell align="right">{name.lastName}</TableCell>
                                <TableCell align="right">{name.country}</TableCell>
                                <TableCell align="right">{name.collegeName}</TableCell>
                                <TableCell align="right">{name.yearsPro}</TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
            }

        </React.Fragment>


    );

};

Search.getInitialProps = async function({store}) {

}

export default Search;
