import React from 'react';
import { connect,useDispatch,useSelector } from 'react-redux';
import StatsDisplay from '../components/StatsDisplay';
import Search from '../components/Search';
import theme from '../src/Theme';
import {decrementCounter, incrementCounter,addPlayers} from '../redux/actions/counterActions';

import {MuiThemeProvider} from '@material-ui/core/styles';
import Typography from "@material-ui/core/Typography";



const Index = (prop) => {
    console.log(prop);
    const dispatch = useDispatch();

    (function addToState() {
        console.log('xxxxxxxx');
        console.log(prop.stats['standard']);
        dispatch(addPlayers());
    })()


    return (
        <MuiThemeProvider theme={theme}>
                   <div>
                       {/*h1 with h5 style*/}
                       <Typography variant="h5" component="h1">
                           Sign up
                       </Typography>

                       <button onClick={()=>dispatch(incrementCounter())}>Increment</button>
                       <button onClick={()=>dispatch(decrementCounter())}>Decrement</button>


                       <Search />

                       {/*pass prop*/}
                       {/*{<StatsDisplay stats={prop.stats}/>}*/}
                       {/*{<StatsDisplay />}*/}
                   </div>
        </MuiThemeProvider>


    );
};


//life cycle method, run when load
Index.getInitialProps = async function({store}) {

    //npm i isomorphic-unfetch
    const res = await fetch('http://data.nba.net/10s/prod/v1/2019/players.json');
    const data = await res.json();

    return {
        stats: data.league
    };
}

export default Index;
