import {INCREMENT_COUNTER,DECREMENT_COUNTER,SAVE_PLAYERS,GET_PLAYERS} from '../actions/types'


const initialState = {
    counter: 0,
    basketBallPlayers: 0
};

const counterReducer = (state= initialState, action) => {
    switch (action.type) {
        case SAVE_PLAYERS:
            return {
                ...state,
                basketBallPlayers: [...state.basketBallPlayers,action.payload]
            };
        case GET_PLAYERS:
            return {
                ...state,
                basketBallPlayers: action.payload
            };
        case INCREMENT_COUNTER:
            return {...state, counter: state.counter + 1};
        case DECREMENT_COUNTER:
            return {...state, counter: state.counter - 1};
        default:
            return {...state};
    }
};

export default counterReducer;
