import {INCREMENT_COUNTER,
    DECREMENT_COUNTER,
    SAVE_PLAYERS,
    GET_PLAYERS,
    SEARCH_PLAYERS} from './types'
import {useDispatch} from 'react-redux';


//Action Creator
export const incrementCounter = () => ({
    type: INCREMENT_COUNTER
});

export const decrementCounter = () => ({
    type: DECREMENT_COUNTER
});

export const addPlayers = basketBallPlayers => ({
    type: SAVE_PLAYERS,
    payload: basketBallPlayers
});

export const searchPlayers = text => async dispatch => {
    dispatch({
        type: SEARCH_PLAYERS,
        payload: text
    })
};

export const getPlayers = () => async dispatch => ({
    type: GET_PLAYERS
});
