import { useImmer } from "use-immer"
import React, {useEffect} from 'react';
import { connect,useDispatch,useSelector } from 'react-redux';
import {searchPlayers,searchPlayerTwo} from '../redux/actions/counterActions';
import Cards from './Cards';
import TextField from '@material-ui/core/TextField';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import {makeStyles} from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';




const useStyles = makeStyles({
    root: {
        minWidth: 275,
        width: '100%'
    },
    columnCard:{
        width: '50%',
        margin: 'auto',
        padding: '.75rem',
        backgroundColor: '#00d1b2',
        color: '#fff',
    },
    bullet: {
        display: 'inline-block',
        margin: '0 2px',
        transform: 'scale(0.8)',
    },
    title: {
        fontSize: '32px',
        fontWeight: '600',
        textAlign: 'center',
        textTransform: 'capitalize',
        color: '#fff',
    },
    subTitle: {
        fontSize: '20px',
        fontWeight: '400',
        textAlign: 'center',
        textTransform: 'capitalize',
        color: '#fff',
    },
    pos: {
        marginBottom: 12,
    },
    flexCenter: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        margin: '100px 0',
        '& > div':{
            width: '40%',
            marginRight: '20px',
        }
    },

});

const Search = (props) => {
    const classes = useStyles();
    const [state, setState] = useImmer({
        searchTerm: "",
        results: [],
        playerOne: null,
        playerTwo: null,
        allPlayers: [],
        id: null,
        show: "neither",
        requestCount: 0,
    });

    const dispatch = useDispatch();
    const  {basketBallPlayers,contestantOne,contestantTwo} = useSelector((state)=>state.counter);

    //get all players on first render
    useEffect(()=>{
        setState((draft) => {
            draft.allPlayers = basketBallPlayers;
        })
    },[])

    //trigger when contestantOne is changed
    useEffect(()=>{
        setState((draft) => {
            draft.playerOne = contestantOne;
        })
    },[contestantOne,contestantTwo])

    //trigger when contestantTwo is changed
    useEffect(()=>{
        setState((draft) => {
            draft.playerTwo = contestantTwo;
        })
    },[contestantTwo])

    //trigger when search term as change
    useEffect(()=>{
        const newPlayer = {
            id: state.id,
            value: state.searchTerm
        }
        dispatch(searchPlayers(newPlayer))
    },[state.searchTerm])


    function handleInput(e) {
        const value = e.target.value
        const id = e.target.id

        let result = state.allPlayers.filter(n=>{
            return n.firstName.toLowerCase().includes(value.toLowerCase());
        })
        setState((draft) => {
            draft.results = result
        })


        /*setState((draft) => {
            draft.searchTerm = value
            draft.id = id;
        })*/
    }


    return (
        <React.Fragment>
            <div className={classes.flexCenter}>
                <div>
                    <form noValidate autoComplete="off">
                        <TextField className={classes.root} id="first" label="Player1" onChange={handleInput} />
                    </form>
                    {Boolean(state.playerOne != null) &&(
                        <Cards playerInformation={state.playerOne}/>
                    )}
                </div>

                <div>

                    <form noValidate autoComplete="off">
                        <TextField className={classes.root} id="second" label="Player2" onChange={handleInput} />
                    </form>

                    {Boolean(state.playerTwo !== null) &&(
                        <Cards playerInformation={state.playerTwo}/>
                    )}


                </div>

            </div>
            {state.searchTerm === "" || state.results.length <= 0 &&
            <Card className={classes.columnCard}>
                <CardContent>
                    <Typography
                        className={classes.title}
                        gutterBottom>
                        Search For A Player On Both Sides
                    </Typography>

                    <Typography
                        className={classes.subTitle}
                        variant="h5" /*h5 in size*/
                        component="h1" /*but and actual h1*/
                    >
                        We will tell you which is best!
                    </Typography>

                </CardContent>
            </Card>
            }

            <ul>
                {state.results.map((name,index) =>
                    <li key={name.personId}>
                        {name.firstName +" "+
                        name.lastName + "=>" + name.personId}
                    </li>
                )}

            </ul>
        </React.Fragment>


    );

};

Search.getInitialProps = async function({store}) {

}

export default Search;
