import { useImmer } from "use-immer"
import React, {useEffect} from 'react';
import { connect,useDispatch,useSelector } from 'react-redux';
import {searchPlayers} from '../redux/actions/counterActions';
import Cards from './Cards';
import TextField from '@material-ui/core/TextField';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import {makeStyles} from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';




const useStyles = makeStyles({
    root: {
        minWidth: 275,
        width: '100%'
    },
    columnCard:{
        backgroundColor: '#00d1b2',
    },
    bullet: {
        display: 'inline-block',
        margin: '0 2px',
        transform: 'scale(0.8)',
    },
    title: {
        fontSize: 14,
    },
    pos: {
        marginBottom: 12,
    },
    flexCenter: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        margin: '100px 0',
        '& > div':{
            width: '40%',
            marginRight: '20px',
        }
    },

});

const Search = (props) => {
    const classes = useStyles();
    const [state, setState] = useImmer({
        searchTerm: "",
        results: [],
        playerOne: {},
        playerTwo: {},
        show: "neither",
        requestCount: 0,
    });

    const dispatch = useDispatch();
    const  {contestantOne} = useSelector((state)=>state.counter);

    useEffect(()=>{
        setState((draft) => {
            draft.playerOne = contestantOne;
        })
        console.log(state.playerOne);
    },[contestantOne])




    function handleInput(e) {
        const value = e.target.value
        console.log(value);
        setState((draft) => {
            draft.searchTerm = value
        })
        dispatch(searchPlayers(value))
    }

    return (
        <React.Fragment>
            <div className={classes.flexCenter}>
                <div>

                    <form noValidate autoComplete="off">
                        <TextField className={classes.root} id="standard-basic" label="Player1" onChange={handleInput} />
                    </form>
                    {state.playerOne ? <Cards playerInformation={state.playerOne}/>:""}
                </div>

                <div>

                    <form noValidate autoComplete="off">
                        <TextField className={classes.root} id="standard-basic" label="Player2" onChange={handleInput} />
                    </form>

                    {state.playerOne ? <Cards playerInformation={state.playerOne}/>:""}


                </div>

            </div>
            {state.playerOne? ""
                :
                <Card className={classes.columnCard}>
                    <CardContent>
                        <Typography className={classes.title} color="textSecondary" gutterBottom>
                            Search For A Player On Both Sides
                        </Typography>

                    </CardContent>
                </Card>
            }
        </React.Fragment>


    );

};

Search.getInitialProps = async function({store}) {

}

export default Search;
