import React, {useEffect, useState} from 'react';
import fetch from 'isomorphic-unfetch';
import {connect,useDispatch,useSelector} from 'react-redux';
import {getSingleRoaster} from '../redux/actions/counterActions';

const Roster = (props) => {
    const dispatch = useDispatch();
    const [state,setState] = useState();
    const {teamRoaster} = useSelector((state)=>state.counter);

    useEffect(()=>{
        dispatch(getSingleRoaster(props.teamID))
        console.log(teamRoaster);
    },[])

    useEffect(()=>{
        console.log(teamRoaster);
        if(teamRoaster !== null){
            setState(teamRoaster);
        }
    },[teamRoaster])


    return (
        <div>
            <h1>{props.teamID}</h1>
        </div>
    );
};

Roster.getInitialProps = async function({store,req,res,query}) {
    const teamName = query.team;

    return {
        teamID: teamName
    };
}
export default Roster;
