import React, {useEffect, useState} from 'react';
import fetch from 'isomorphic-unfetch';
import {connect,useDispatch,useSelector} from 'react-redux';
import {getSingleRoaster,getAllPlayerId} from '../redux/actions/counterActions';
import { useImmer } from "use-immer"
import Cards from '../components/Cards';

//--//
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';


const useStyles = makeStyles({
    table: {
        minWidth: 650,
    },
});

const Roster = (props) => {
    const dispatch = useDispatch();
    const {teamRoaster,personIds} = useSelector((state)=>state.counter);
    const [state, setState] = useImmer({
        isLoading: true,
        players: []
    })

    useEffect(()=>{
        dispatch(getSingleRoaster(props.teamID))
    },[])

    useEffect(()=>{
         if(!teamRoaster){
             console.log('run something here');
         } else {
             dispatch(getAllPlayerId('1610612750'));
         }
    },[teamRoaster])

    useEffect(()=>{
        if(personIds){
            console.log(personIds.league.standard.players)
            setState(draft => {
                draft.players = personIds.league.standard.players
            })
        }
    },[personIds])


    return (

        <div>
            {state.players.length > 0 && (
                    <div>
                        {state.players.map(post => {
                            return <Cards key={post.personId} post={post}/>
                        })}
                    </div>
            )}
        </div>
    );
};

Roster.getInitialProps = async function({store,req,res,query}) {
    const teamName = query.team;

    return {
        teamID: teamName
    };
}
export default Roster;
